import React from 'react'
import { useState } from 'react'
import Editprofile from './Editprofile/Editprofile.js';
import './editorfollow.css'

function Editorfollow({ username, viewing }) {
  const [isEdit, setEdit] = useState(false);

  const returntoprofile = () => {
    document.getElementById("editpg").style.transform = "translateX(-100%)";
    setTimeout(() => {
      setEdit(false);
    }, 550);
  }
  const gotoeditprofile = () => {
    setEdit(true);
    setTimeout(() => {
      document.getElementById("editpg").style.transform = "translateX(0%)";
    }, 100);
  }
  
  return (
    <div className='editorfollow'>
      {isEdit ? (
        <Editprofile returntoprofile={returntoprofile}/>
      ) : (null
      )}
      {viewing === username ? (
        <div className='editprofile'>
          <button className='editbtn' onClick={gotoeditprofile}>Edit Profile</button>
        </div>
      ) : (
        <div className='follow'>
          <button className='followbtn'>Follow</button>
        </div>
      )}
    </div>
  )
}

export default Editorfollow
