import {React, useState} from 'react'
import './Editpost.css'

function Editpost({ origphoto }) {
  const [blur, setBlur] = useState(0);
  const [brightness, setBrightness] = useState(100);
  const [contrast, setContrast] = useState(100);
  const [grayscale, setGrayscale] = useState(0);
  const [opacity, setOpacity] = useState(100);
  const [saturate, setSaturate] = useState(100);

  const applyFilter = () => {
    let videoelement = document.querySelector('.stream');
    videoelement.style.filter = 'blur('+blur+'px) brightness('+brightness+'%) contrast('+contrast+'%) grayscale('+grayscale+'%) opacity('+opacity+'%) saturate('+saturate+'%)';
  };

  return (
    <div className='Editpost'>
      <img src={origphoto} alt='origphoto' className='stream'/>
      {origphoto}
        <div className='filters'>
          <label htmlFor="blur">Blur: {blur}</label>
          <input type="range" id="blur" name="blur" min="0" max="10" value={blur} onChange={(e) => setBlur(e.target.value)} />
          <label htmlFor="brightness">Brightness: {brightness}</label>
          <input type="range" id="brightness" name="brightness" min="0" max="200" value={brightness} onChange={(e) => setBrightness(e.target.value)} />
          <label htmlFor="contrast">Contrast: {contrast}</label>
          <input type="range" id="contrast" name="contrast" min="0" max="200" value={contrast} onChange={(e) => setContrast(e.target.value)} />
          <label htmlFor="grayscale">Grayscale: {grayscale}</label>
          <input type="range" id="grayscale" name="grayscale" min="0" max="100" value={grayscale} onChange={(e) => setGrayscale(e.target.value)} />
          <label htmlFor="opacity">Opacity: {opacity}</label>
          <input type="range" id="opacity" name="opacity" min="0" max="100" value={opacity} onChange={(e) => setOpacity(e.target.value)} />
          <label htmlFor="saturate">Saturate: {saturate}</label>
          <input type="range" id="saturate" name="saturate" min="0" max="200" value={saturate} onChange={(e) => setSaturate(e.target.value)} />
          <input type="button" value="Apply Filter" onClick={applyFilter} />
        </div>
    </div>
  )
}

export default Editpost
