import React, { useState, useEffect, useRef } from 'react'
import './Takepost.css'

function Takepost() {
  const [stream, setStream] = useState(null);
  const [photo, setPhoto] = useState(null);
  const [showLiveFeed, setShowLiveFeed] = useState(true);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    async function getMediaStream() {
      try {
        const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });
        setStream(mediaStream);
      } catch (error) {
        console.error('Error accessing camera:', error);
      }
    }

    getMediaStream();

    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  useEffect(() => {
    if (videoRef.current && stream) {
      videoRef.current.srcObject = stream;
    }
  }, [stream]);

  const takePhoto = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    const context = canvas.getContext('2d');

    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    const data = canvas.toDataURL('image/png');
    setPhoto(data);
    setShowLiveFeed(false);
  };

  const retakePhoto = () => {
    setPhoto(null);
    setShowLiveFeed(true);
  };

  return (
    <div className='Takepost'>
      {showLiveFeed && <video ref={videoRef} autoPlay muted />}
      <canvas ref={canvasRef} style={{ display: 'none' }} />
      {showLiveFeed && <button onClick={takePhoto}>Take Photo</button>}
      {photo && <img src={photo} alt='User Taken' />}
      {photo && <button onClick={retakePhoto}>Retake Photo</button>}
    </div>
  )
}

export default Takepost
