import React, { useState, useEffect } from 'react';

const Takepost = () => {
  const [stream, setStream] = useState(null);
  const [photo, setPhoto] = useState(null);

  useEffect(() => {
    // Get user media when the component mounts
    async function getCamera() {
      try {
        const userMedia = await navigator.mediaDevices.getUserMedia({ video: true });
        setStream(userMedia);
      } catch (error) {
        console.error('Error accessing the camera:', error);
      }
    }
    getCamera();

    // Clean up the camera stream when the component unmounts
    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  const takePhoto = () => {
    if (stream) {
      const videoTrack = stream.getVideoTracks()[0];
      const imageCapture = new ImageCapture(videoTrack);
      imageCapture
        .takePhoto()
        .then((blob) => {
          setPhoto(URL.createObjectURL(blob));
        })
        .catch((error) => {
          console.error('Error taking photo:', error);
        });
    }
  };

  return (
    <div>
      {stream ? (
        <div>
          <video autoPlay={true} playsInline={true} srcObject={stream}></video>
          <button onClick={takePhoto}>Take Photo</button>
        </div>
      ) : (
        <p>Access to the camera is denied or unavailable.</p>
      )}
      {photo && <img src={photo} alt="Captured" />}
    </div>
  );
};

export default Takepost;
