import React, { useState, useEffect, useRef } from 'react'
import './Takepost.css'

function Takepost() {
  const [showCamera, setShowCamera] = useState(true)
  const [photoTaken, setPhotoTaken] = useState(false)

  const videoRef = useRef(null)
  const canvasRef = useRef(null)
  const imgRef = useRef(null)

  useEffect(() => {
    const enableCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true })
        videoRef.current.srcObject = stream
        // Listen for the 'canplay' event to ensure the video is ready
        videoRef.current.addEventListener('canplay', () => {
          // You can set a flag here to indicate that the video is ready
          // if needed
        })
      } catch (error) {
        setShowCamera(false)
        console.error(error)
      }
    }
    enableCamera()
  }, [])
  
  const takePhoto = () => {
    const canvas = canvasRef.current
    const video = videoRef.current
  
    // Check if the video is ready before capturing
    if (video.readyState === 4) {
      canvas.width = video.videoWidth
      canvas.height = video.videoHeight
      canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height)
      const dataURL = canvas.toDataURL('image/png')
      setPhotoTaken(true)
      setShowCamera(false)
      imgRef.current.src = dataURL
    }
  }
  

  return (
    <div className='Takepost'>
      {showCamera ? (
        <>
          <video ref={videoRef} autoPlay playsInline />
          <canvas ref={canvasRef} />
          <button onClick={takePhoto}>Take Photo</button>
        </>
      ) : (
        <>
          <img ref={imgRef} alt="Taken Photo" />
          <button onClick={() => {setShowCamera(true); setPhotoTaken(false)}}>Retake Photo</button>
        </>
      )}
    </div>
  )
}

export default Takepost
