import React, { useState, useEffect, useRef } from 'react'
import './Takepost.css'

function Takepost() {
  const [stream, setStream] = useState(null);
  const [photo, setPhoto] = useState(null);
  const [brightness, setBrightness] = useState(100);
  const [contrast, setContrast] = useState(100);
  const [grayscale, setGrayscale] = useState(0);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    async function getMediaStream() {
      try {
        const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });
        setStream(mediaStream);
      } catch (error) {
        console.error('Error accessing camera:', error);
      }
    }

    getMediaStream();

    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  useEffect(() => {
    if (videoRef.current && stream) {
      videoRef.current.srcObject = stream;
    }
  }, [stream]);

  const takePhoto = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    const context = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.filter = `brightness(${brightness}%) contrast(${contrast}%) grayscale(${grayscale}%)`;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    const data = canvas.toDataURL('image/png');
    setPhoto(data);
    console.log(data);
  };

  const handleBrightnessChange = (event) => {
    setBrightness(event.target.value);
  };

  const handleContrastChange = (event) => {
    setContrast(event.target.value);
  };

  const handleGrayscaleChange = (event) => {
    setGrayscale(event.target.value);
  };

  return (
    <div className='Takepost'>
        <video ref={videoRef} autoPlay muted style={{ transform: 'scaleX(-1)' }} className='stream'/>
        <canvas ref={canvasRef} style={{ display: 'none' }} />
        <div className='sliders'>
          <label htmlFor='brightness'>Brightness:</label>
          <input type='range' id='brightness' name='brightness' min='0' max='200' value={brightness} onChange={handleBrightnessChange} />
          <label htmlFor='contrast'>Contrast:</label>
          <input type='range' id='contrast' name='contrast' min='0' max='200' value={contrast} onChange={handleContrastChange} />
          <label htmlFor='grayscale'>Grayscale:</label>
          <input type='range' id='grayscale' name='grayscale' min='0' max='100' value={grayscale} onChange={handleGrayscaleChange} />
        </div>
        <button onClick={takePhoto} className='takephoto'></button>
    </div>
  )
}

export default Takepost
