import React, { useState, useEffect, useRef } from 'react'
import './Takepost.css'

function Takepost() {
  const [stream, setStream] = useState(null);
  const [photo, setPhoto] = useState(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [blur, setBlur] = useState(0);
  const [brightness, setBrightness] = useState(100);
  const [contrast, setContrast] = useState(100);
  const [dropshadow, setDropshadow] = useState(0);
  const [grayscale, setGrayscale] = useState(0);
  const [huerotate, setHuerotate] = useState(0);
  const [invert, setInvert] = useState(0);
  const [opacity, setOpacity] = useState(100);
  const [saturate, setSaturate] = useState(100);
  const [sepia, setSepia] = useState(0);

  useEffect(() => {
    async function getMediaStream() {
      try {
        const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });
        setStream(mediaStream);
      } catch (error) {
        console.error('Error accessing camera:', error);
      }
    }

    getMediaStream();

    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  useEffect(() => {
    if (videoRef.current && stream) {
      videoRef.current.srcObject = stream;
    }
  }, [stream]);

  const applyFilter = () => {
    const video = document.querySelector('video');
    console.log('blur('+blur+'px) brightness('+brightness+'%) contrast('+contrast+'%) drop-shadow('+dropshadow+'px '+dropshadow+'px '+dropshadow+'px rgba(0,0,0,0.5)) grayscale('+grayscale+'%) hue-rotate('+huerotate+'deg) invert('+invert+'%) opacity('+opacity+'%) saturate('+saturate+'%) sepia('+sepia+'%)');
    video.style.filter = 'blur('+blur+'px) brightness('+brightness+'%) contrast('+contrast+'%) drop-shadow('+dropshadow+'px '+dropshadow+'px '+dropshadow+'px rgba(0,0,0,0.5)) grayscale('+grayscale+'%) hue-rotate('+huerotate+'deg) invert('+invert+'%) opacity('+opacity+'%) saturate('+saturate+'%) sepia('+sepia+'%)';
  };

  const takePhoto = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    const context = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.filter = `blur(${blur}px) brightness(${brightness}%) contrast(${contrast}%) drop-shadow(${dropshadow}px ${dropshadow}px ${dropshadow}px rgba(0,0,0,0.5)) grayscale(${grayscale}%) hue-rotate(${huerotate}deg) invert(${invert}%) opacity(${opacity}%) saturate(${saturate}%) sepia(${sepia}%)`;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    const data = canvas.toDataURL('image/png');
    setPhoto(data);
    console.log(data);
  };

  return (
    <div className='Takepost'>
        <video ref={videoRef} autoPlay muted style={{ transform: 'scaleX(-1)' }} className='stream'/>
        <canvas ref={canvasRef} style={{ display: 'none' }} />
        <div className='filters'>
          <label htmlFor="blur">Blur:</label>
          <input type="range" id="blur" name="blur" min="-1" max="10" value={blur} onChange={(e) => {setBlur(e.target.value); applyFilter()}} />
          <label htmlFor="brightness">Brightness:</label>
          <input type="range" id="brightness" name="brightness" min="0" max="200" value={brightness} onChange={(e) => setBrightness(e.target.value)} />
          <label htmlFor="contrast">Contrast:</label>
          <input type="range" id="contrast" name="contrast" min="0" max="200" value={contrast} onChange={(e) => setContrast(e.target.value)} />
          <label htmlFor="dropshadow">Drop Shadow:</label>
          <input type="range" id="dropshadow" name="dropshadow" min="0" max="10" value={dropshadow} onChange={(e) => setDropshadow(e.target.value)} />
          <label htmlFor="grayscale">Grayscale:</label>
          <input type="range" id="grayscale" name="grayscale" min="0" max="100" value={grayscale} onChange={(e) => setGrayscale(e.target.value)} />
          <label htmlFor="huerotate">Hue Rotate:</label>
          <input type="range" id="huerotate" name="huerotate" min="0" max="360" value={huerotate} onChange={(e) => setHuerotate(e.target.value)} />
          <label htmlFor="invert">Invert:</label>
          <input type="range" id="invert" name="invert" min="0" max="100" value={invert} onChange={(e) => setInvert(e.target.value)} />
          <label htmlFor="opacity">Opacity:</label>
          <input type="range" id="opacity" name="opacity" min="0" max="100" value={opacity} onChange={(e) => setOpacity(e.target.value)} />
          <label htmlFor="saturate">Saturate:</label>
          <input type="range" id="saturate" name="saturate" min="0" max="200" value={saturate} onChange={(e) => setSaturate(e.target.value)} />
          <label htmlFor="sepia">Sepia:</label>
          <input type="range" id="sepia" name="sepia" min="0" max="100" value={sepia} onChange={(e) => setSepia(e.target.value)} />
        </div>
        <button onClick={takePhoto} className='takephoto'></button>
    </div>
  )
}

export default Takepost
